import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Navbar.css';

const Navbar = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  const toggleDropdown = () => setDropdownOpen(!dropdownOpen);
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
    setDropdownOpen(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setDropdownOpen(false);
    setMenuOpen(false);
    navigate('/');
  };

  const handleLogin = () => {
    setMenuOpen(false);
    setDropdownOpen(false);
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <h1 className="logo">Sakura E-Commerce</h1>
      </div>

      {/* Hamburger Icon */}
      <button className="menu-toggle" onClick={toggleMenu} aria-label="Toggle menu">
        ☰
      </button>

      {/* Desktop Nav Links */}
      <ul className="nav-links">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/products">Products</Link></li>
        <li><Link to="/about">About</Link></li>
        <li><Link to="/contact">Contact</Link></li>
      </ul>

      {/* Desktop Actions */}
      <div className="navbar-actions">
        <button className="cart-button">🛒 Cart</button>
        <div className="profile-dropdown">
          <button onClick={toggleDropdown} className="profile-button">▼</button>
          {dropdownOpen && (
            <ul className="dropdown-menu">
              <li><Link to="/profile">My Profile</Link></li>
              <li><Link to="/orders">My Orders</Link></li>
            </ul>
          )}
        </div>
        {isLoggedIn ? (
          <button onClick={handleLogout} className="login-button">Logout</button>
        ) : (
          <button onClick={handleLogin} className="login-button">Login</button>
        )}
      </div>

      {/* Fullscreen Mobile Menu */}
      <div className={`fullscreen-menu ${menuOpen ? 'open' : ''}`}>
        <button className="close-menu" onClick={toggleMenu}>✕</button>
        <ul className="fullscreen-menu-items">
          <li><Link to="/" onClick={toggleMenu}>Home</Link></li>
          <li><Link to="/products" onClick={toggleMenu}>Products</Link></li>
          <li><Link to="/about" onClick={toggleMenu}>About</Link></li>
          <li><Link to="/contact" onClick={toggleMenu}>Contact</Link></li>
          <li>
            <button onClick={toggleDropdown} className="profile-button">
              Profile ▼
            </button>
            {dropdownOpen && (
              <ul className="mobile-dropdown-menu">
                <li><Link to="/profile" onClick={toggleMenu}>My Profile</Link></li>
                <li><Link to="/orders" onClick={toggleMenu}>My Orders</Link></li>
              </ul>
            )}
          </li>
          <li>
            {isLoggedIn ? (
              <button onClick={handleLogout} className="login-button">Logout</button>
            ) : (
              <button onClick={handleLogin} className="login-button">Login</button>
            )}
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
