import React, { useEffect, useState } from 'react';
import '../styles/AllMessages.css';
import adminApi from '../services/adminApi';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';  // <-- named import

const isAdmin = () => {
  const token = localStorage.getItem('adminToken');
  if (!token) return false;
  try {
    const decoded = jwtDecode(token);  // <-- use named import function
    return decoded.role === 'admin';
  } catch {
    return false;
  }
};

const AllMessages = () => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (!isAdmin()) {
      navigate('/login-admin');
      return;
    }

    async function fetchMessages() {
      try {
        const res = await adminApi.get('/message');
        setMessages(res.data.response || []);
      } catch (err) {
        setError(err.response?.data?.message || err.message || 'Failed to fetch messages');
      } finally {
        setLoading(false);
      }
    }
    fetchMessages();
  }, [navigate]);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this message?')) return;

    try {
      await adminApi.delete(`/message/${id}`);
      setMessages(messages.filter(msg => msg._id !== id));
    } catch (err) {
      alert(err.response?.data?.message || err.message || 'Failed to delete message');
    }
  };

  if (loading) return <p className="loading">Loading messages...</p>;
  if (error) return <p className="error-message">Error: {error}</p>;
  if (messages.length === 0) return <p className="no-message">No messages found.</p>;

  return (
    <div className="all-messages-container">
      <h2>All Messages</h2>
      <ul className="message-list">
        {messages.map(msg => (
          <li key={msg._id} className="message-item">
            <div className="message-content">
              <p><strong>From:</strong> {msg.senderName || 'Unknown'}</p>
              <p><strong>Email:</strong> {msg.email || 'N/A'}</p>
              <p><strong>Message:</strong> {msg.message || '-'}</p>
              <small>{new Date(msg.createdAt).toLocaleString()}</small>
            </div>
            <button className="delete-btn" onClick={() => handleDelete(msg._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AllMessages;
