import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/UpdateOrderForm.css';
import { jwtDecode } from 'jwt-decode';

const isAdmin = () => {
  const token = localStorage.getItem('adminToken');
  if (!token) return false;
  try {
    const decoded = jwtDecode(token);
    return decoded.role === 'admin';
  } catch {
    return false;
  }
};

const UpdateOrderForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [order, setOrder] = useState(null);
  const [status, setStatus] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!isAdmin()) {
      navigate('/login-admin');
      return;
    }

    const token = localStorage.getItem('adminToken');

    const fetchOrder = async () => {
      try {
        const response = await fetch(`/api/admin/orders/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          const errData = await response.json();
          throw new Error(errData.message || 'Failed to fetch order');
        }

        const data = await response.json();
        setOrder(data.order);
        setStatus(data.order.status || '');
        setError('');
      } catch (err) {
        setError(err.message || 'Failed to fetch order');
      } finally {
        setLoading(false);
      }
    };

    fetchOrder();
  }, [id, navigate]);

  const handleStatusChange = (e) => {
    setStatus(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    const token = localStorage.getItem('adminToken');

    try {
      const response = await fetch(`http://localhost:5000/api/admin/update-products/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status }),
      });

      if (!response.ok) {
        const errData = await response.json();
        throw new Error(errData.message || 'Update failed');
      }

      alert('Order status updated successfully');
      navigate('/admin/update-order');
    } catch (err) {
      setError(err.message || 'Update failed');
    }
  };

  if (loading) return <p>Loading order data...</p>;
  if (error) return <p style={{ color: 'red' }}>Error: {error}</p>;
  if (!order) return <p>No order found.</p>;

  return (
    <div className="update-order-form-container">
      <h2>Update Order Status</h2>
      <div className="order-details">
        <p><strong>Order ID:</strong> {order._id}</p>
        <p><strong>User:</strong> {order.user?.name || 'Guest'} ({order.user?.email || 'N/A'})</p>
        <p><strong>Payment Method:</strong> {order.paymentMethod}</p>
        <p><strong>Shipping Info:</strong> {order.shippingInfo?.name}, {order.shippingInfo?.address}</p>
        <p><strong>Items:</strong></p>
        <ul>
          {order.items.map((item, idx) => (
            <li key={idx}>
              {item.productId?.name || 'Product'} × {item.quantity}
            </li>
          ))}
        </ul>
      </div>

      <form onSubmit={handleSubmit} className="update-order-form">
        <label htmlFor="status">Status:</label>
        <select id="status" value={status} onChange={handleStatusChange} required>
          <option value="">Select status</option>
          <option value="Pending">Pending</option>
          <option value="Processing">Processing</option>
          <option value="Shipped">Shipped</option>
          <option value="Delivered">Delivered</option>
          <option value="Cancelled">Cancelled</option>
        </select>

        <button type="submit">Update Status</button>
      </form>
    </div>
  );
};

export default UpdateOrderForm;
