import React, { useState, useEffect } from 'react';
import { Routes, Route, useLocation } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import './styles/App.css';

import Navbar from './components/Navbar';
import Footer from './components/Footer';

import LoginPage from './pages/LoginPage';
import SignupPage from './pages/SignupPage';
import ProfilePage from './pages/ProfilePage';
import HomePage from './pages/HomePage';
import AllProduct from './pages/AllProduct';
import MensCategory from './pages/MensCategory';
import WomensCategory from './pages/WomensCategory';
import KidsCategory from './pages/KidsCategory';
import ContactUs from './pages/ContactUs';
import AboutUs from './pages/AboutUs';
import Cart from './pages/Cart';

// Products admin pages
import ProductList from './admin/pages/ProductList';
import AddProduct from './admin/pages/AddProduct';
import EditProduct from './admin/pages/UpdateProduct';

// Orders admin pages
import OrderList from './admin/pages/AllOrders';
import EditOrder from './admin/pages/UpdateOrder';

import MessageList from './admin/pages/AllMessages';
import Checkout from './pages/Checkout';
import Payment from './pages/Payment';
import OrderSuccess from './pages/OrderSuccess';
import MyOrderPage from './pages/MyOrderPage';
import DetailedProduct from './pages/DetailedProduct';
import AdminPanel from './admin/AdminLayout';
import Sidebar from './admin/Sidebar';
import AdminLayout from './admin/AdminLayout';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
      return;
    }
    try {
      const decoded = jwtDecode(token);
      if (decoded.exp * 1000 < Date.now()) {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
      } else {
        setIsLoggedIn(true);
      }
    } catch (error) {
      localStorage.removeItem('token');
      setIsLoggedIn(false);
    }
  }, [location]);

  const isAdminRoute = location.pathname.startsWith('/admin');

  return (
    <div className="App">
      {!isAdminRoute && <Navbar isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />}

      <Routes>
        {/* Public routes */}
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginPage setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="/signup" element={<SignupPage />} />
        <Route path="/profile" element={<ProfilePage />} />
        <Route path="/orders" element={<MyOrderPage />} />
        <Route path="/products" element={<AllProduct />} />
        <Route path="/men" element={<MensCategory />} />
        <Route path="/women" element={<WomensCategory />} />
        <Route path="/kids" element={<KidsCategory />} />
        <Route path="/contact" element={<ContactUs />} />
        <Route path="/about" element={<AboutUs />} />
        <Route path="/cart" element={<Cart />} />
        <Route path="/checkout" element={<Checkout />} />
        <Route path="/payment" element={<Payment />} />
        <Route path="/order-success" element={<OrderSuccess />} />
        <Route path="/product/:id" element={<DetailedProduct />} />



        {/* Admin routes */}
        <Route path="/admin" element={<AdminLayout />} />
        <Route path="/admin" element={<Sidebar />} />
        <Route path="/admin/products" element={<ProductList />} />
        <Route path="/admin/products/add" element={<AddProduct />} />
        <Route path="/admin/products/edit/:id" element={<EditProduct />} />
        <Route path="/admin/orders" element={<OrderList />} />
        <Route path="/admin/orders/edit/:id" element={<EditOrder />} />
        <Route path="/admin/messages" element={<MessageList />} />
      </Routes>

      {!isAdminRoute && <Footer />}
    </div>
  );
}

export default App;
