import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function ProductDetail() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/products/${id}`)
      .then(res => setProduct(res.data.response))
      .catch(err => console.error(err));
  }, [id]);

  if (!product) return <div className="p-4">Loading...</div>;

  return (
    <div className="p-4 max-w-3xl mx-auto">
      <img src={product.imageUrl} alt={product.name} className="w-full h-64 object-cover rounded" />
      <h2 className="text-2xl font-bold mt-4">{product.name}</h2>
      <p className="text-gray-700">{product.description}</p>
      <p className="text-green-600 font-semibold mt-2">${product.price}</p>
      <p className="text-sm text-gray-500 mt-1">Category: {product.category}</p>
      <p className="text-sm text-gray-500">Stock: {product.stock}</p>
      <p className="text-sm text-gray-400">Added on: {new Date(product.createdAt).toLocaleDateString()}</p>
    </div>
  );
}

export default ProductDetail;
