import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Navbar.css';

const Navbar = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false); 
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  const toggleDropdown = () => setDropdownOpen(!dropdownOpen);
  const toggleMenu = () => setMenuOpen(!menuOpen);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setDropdownOpen(false);
    setMenuOpen(false);
    navigate('/');
  };

  const handleLogin = () => {
    setMenuOpen(false);
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <h1 className="logo">Sakura E-Commerce</h1>
      </div>

      {/* Hamburger Icon */}
      <button className="menu-toggle" onClick={toggleMenu} aria-label="Toggle menu">
        â˜°
      </button>

      {/* Desktop Nav Links */}
      <ul className="nav-links">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/products">Products</Link></li>
        <li><Link to="/about">About</Link></li>
        <li><Link to="/contact">Contact</Link></li>
      </ul>

      {/* Desktop Actions */}
      <div className="navbar-actions">
        <button className="cart-button">ðŸ›’ Cart</button>
        <div className="profile-dropdown">
          <button onClick={toggleDropdown} className="profile-button">â–¼</button>
          {dropdownOpen && (
            <ul className="dropdown-menu">
              <li><Link to="/profile">My Profile</Link></li>
              <li><Link to="/orders">My Orders</Link></li>
            </ul>
          )}
        </div>
        {isLoggedIn ? (
          <button onClick={handleLogout} className="login-button">Logout</button>
        ) : (
          <button onClick={handleLogin} className="login-button">Login</button>
        )}
      </div>

      {/* Mobile Dropdown Menu */}
      {menuOpen && (
        <div className="mobile-menu">
          <ul>
            <li><Link to="/" onClick={() => setMenuOpen(false)}>Home</Link></li>
            <li><Link to="/products" onClick={() => setMenuOpen(false)}>Products</Link></li>
            <li><Link to="/about" onClick={() => setMenuOpen(false)}>About</Link></li>
            <li><Link to="/contact" onClick={() => setMenuOpen(false)}>Contact</Link></li>
            <li>
              <button onClick={toggleDropdown} className="profile-button mobile-profile-btn">
                Profile â–¼
              </button>
              {dropdownOpen && (
                <ul className="dropdown-menu mobile-dropdown-menu">
                  <li><Link to="/profile" onClick={() => setMenuOpen(false)}>My Profile</Link></li>
                  <li><Link to="/orders" onClick={() => setMenuOpen(false)}>My Orders</Link></li>
                </ul>
              )}
            </li>
            <li>
              {isLoggedIn ? (
                <button onClick={handleLogout} className="login-button mobile-login-btn">Logout</button>
              ) : (
                <button onClick={handleLogin} className="login-button mobile-login-btn">Login</button>
              )}
            </li>
          </ul>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
