import React, { useEffect, useState } from 'react';

const UpdateProduct = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchProducts() {
      try {
        const res = await fetch('http://localhost:5000/api/products');
        if (!res.ok) throw new Error('Failed to fetch products');
        const data = await res.json();
        setProducts(data.response || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchProducts();
  }, []);

  const handleEditClick = (productId) => {
    // For now just log the product ID. 
    // You can later add navigation or open a form here.
    console.log('Edit product with ID:', productId);
  };

  if (loading) return <p>Loading products...</p>;
  if (error) return <p style={{ color: 'red' }}>Error: {error}</p>;

  return (
    <div style={{ maxWidth: 960, margin: 'auto', padding: 20 }}>
      <h2>All Products</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit,minmax(250px,1fr))', gap: 20 }}>
        {products.map(product => (
          <div key={product._id} style={{ border: '1px solid #ccc', padding: 15, borderRadius: 8 }}>
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p><b>Price:</b> ${product.price}</p>
            <button onClick={() => handleEditClick(product._id)}>Edit</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UpdateProduct;
