import React from "react";
import { Link, Outlet } from "react-router-dom";

export default function AdminLayout() {
  return (
    <div style={{ display: "flex", height: "100vh" }}>
      {/* Sidebar */}
      <aside style={{ width: "200px", background: "#1e1e2f", color: "white", padding: "1rem" }}>
        <h2 style={{ marginBottom: "1rem" }}>Admin Panel</h2>
        <nav>
          <ul style={{ listStyle: "none", padding: 0 }}>
            <li><Link to="/admin" style={{ color: "white" }}>Dashboard</Link></li>
            <li><Link to="/admin/products" style={{ color: "white" }}>Products</Link></li>
            <li><Link to="/admin/orders" style={{ color: "white" }}>Orders</Link></li>
            <li><Link to="/admin/messages" style={{ color: "white" }}>Messages</Link></li>
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <main style={{ flex: 1, padding: "1rem" }}>
        <Outlet />
      </main>
    </div>
  );
}
