import React, { useState } from "react";
import "../styles/Dashboard.css";

import AddProduct from "./products/AddProduct";
import ProductList from "./products/ProductList";

import OrderList from "./orders/OrderList";
import EditOrder from "./orders/EditOrder";

import MessageList from "./messages/MessageList";

const Dashboard = () => {
  const [menu, setMenu] = useState("products");
  const [subMenu, setSubMenu] = useState("addProduct");

  const handleMenuClick = (item) => {
    setMenu(item);
    if (item === "products") setSubMenu("addProduct");
    else if (item === "orders") setSubMenu("allOrders");
  };

  const renderProductTabs = () => (
    <div className="tab-bar">
      <button
        className={subMenu === "addProduct" ? "tab active" : "tab"}
        onClick={() => setSubMenu("addProduct")}
      >
        Add Product
      </button>
      <button
        className={subMenu === "updateProduct" ? "tab active" : "tab"}
        onClick={() => setSubMenu("updateProduct")}
      >
        Update Product
      </button>
    </div>
  );

  const renderOrderTabs = () => (
    <div className="tab-bar">
      <button
        className={subMenu === "allOrders" ? "tab active" : "tab"}
        onClick={() => setSubMenu("allOrders")}
      >
        All Orders
      </button>
      <button
        className={subMenu === "updateOrder" ? "tab active" : "tab"}
        onClick={() => setSubMenu("updateOrder")}
      >
        Update Order
      </button>
    </div>
  );

  const renderContent = () => {
    if (menu === "products") {
      return (
        <>
          {renderProductTabs()}
          {subMenu === "addProduct" && <AddProduct />}
          {subMenu === "updateProduct" && <ProductList />}
        </>
      );
    } else if (menu === "orders") {
      return (
        <>
          {renderOrderTabs()}
          {subMenu === "allOrders" && <OrderList />}
          {subMenu === "updateOrder" && <EditOrder />}
        </>
      );
    } else if (menu === "messages") {
      return <MessageList />;
    } else {
      return <div>Welcome to Admin Panel</div>;
    }
  };

  return (
    <div className="dashboard-container">
      <nav className="sidebar">
        <h2 className="logo">Admin Panel</h2>
        <button
          className={menu === "products" ? "sidebar-btn active" : "sidebar-btn"}
          onClick={() => handleMenuClick("products")}
        >
          Products
        </button>
        <button
          className={menu === "orders" ? "sidebar-btn active" : "sidebar-btn"}
          onClick={() => handleMenuClick("orders")}
        >
          Orders
        </button>
        <button
          className={menu === "messages" ? "sidebar-btn active" : "sidebar-btn"}
          onClick={() => handleMenuClick("messages")}
        >
          Messages
        </button>
      </nav>

      <main className="content-area">{renderContent()}</main>
    </div>
  );
};

export default Dashboard;
