import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Dashboard.css';

function Dashboard() {
  const [stats, setStats] = useState({
    productsCount: 0,
    ordersCount: 0,
    messagesCount: 0,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchStats() {
      try {
        const [productsRes, ordersRes, messagesRes] = await Promise.all([
          fetch('http://localhost:5000/api/products/count'),
          fetch('http://localhost:5000/api/orders/count'),
          fetch('http://localhost:5000/api/messages/count'),
        ]);

        if (!productsRes.ok || !ordersRes.ok || !messagesRes.ok) {
          throw new Error('Failed to fetch stats');
        }

        const productsData = await productsRes.json();
        const ordersData = await ordersRes.json();
        const messagesData = await messagesRes.json();

        setStats({
          productsCount: productsData.count || 0,
          ordersCount: ordersData.count || 0,
          messagesCount: messagesData.count || 0,
        });
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchStats();
  }, []);

  if (loading) return <p className="loading">Loading dashboard...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="dashboard-container">
      <h1>Admin Dashboard</h1>
      <div className="stats-cards">
        <Link to="/admin/products" className="stat-card">
          <h2>{stats.productsCount}</h2>
          <p>Products</p>
        </Link>
        <Link to="/admin/orders" className="stat-card">
          <h2>{stats.ordersCount}</h2>
          <p>Orders</p>
        </Link>
        <Link to="/admin/messages" className="stat-card">
          <h2>{stats.messagesCount}</h2>
          <p>Messages</p>
        </Link>
      </div>
    </div>
  );
}

export default Dashboard;
