import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Cart.css';

const Cart = () => {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [removingItemId, setRemovingItemId] = useState(null);
  const [updatingItemId, setUpdatingItemId] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please log in to view your cart.');
      navigate('/login');
    } else {
      fetchCart(token);
    }
  }, [navigate]);


  const fetchCart = async () => {
    try {
      setLoading(true);
      setError('');
      const token = localStorage.getItem('token');

      const res = await fetch('http://localhost:5000/api/cart', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!res.ok) throw new Error('Failed to fetch cart');
      const data = await res.json();
      setCart(data.cart);
    } catch (err) {
      setError(err.message || 'Something went wrong');
    } finally {
      setLoading(false);
    }
  };

  const handleRemove = async (productId) => {
    try {
      setRemovingItemId(productId);
      setError('');
      const token = localStorage.getItem('token');

      const res = await fetch('http://localhost:5000/api/cart/remove', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ productId }),
      });

      if (!res.ok) {
        const errData = await res.json();
        throw new Error(errData.message || 'Failed to remove item');
      }

      const data = await res.json();
      setCart(data.cart);
    } catch (err) {
      setError(err.message || 'Failed to remove item');
    } finally {
      setRemovingItemId(null);
    }
  };

  const handleQuantityChange = async (productId, newQuantity) => {
    if (newQuantity < 1) return;

    try {
      setUpdatingItemId(productId);
      setError('');
      const token = localStorage.getItem('token');

      const res = await fetch('http://localhost:5000/api/cart/update', {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ productId, quantity: newQuantity }),
      });

      if (!res.ok) {
        const errData = await res.json();
        throw new Error(errData.message || 'Failed to update quantity');
      }

      const data = await res.json();
      setCart(data.cart);
    } catch (err) {
      setError(err.message || 'Failed to update quantity');
    } finally {
      setUpdatingItemId(null);
    }
  };

  const handleCheckout = () => {
    navigate('/checkout');
  };

  const totalPrice =
    cart?.items.reduce((total, item) => {
      if (!item.productId) return total;
      return total + item.productId.price * item.quantity;
    }, 0) || 0;

  if (loading) return <p className="cart-status">Loading cart...</p>;
  if (error) return <p className="cart-error">{error}</p>;
  if (!cart || cart.items.length === 0)
    return <p className="cart-status">Your cart is empty.</p>;

  return (
    <div className="cart-container">
      <h1>Your Shopping Cart</h1>
      <div className="cart-items">
        {cart.items.map(({ productId, quantity }) => (
          <div className="cart-item" key={productId._id}>
            <img
              src={productId.imageUrl || 'https://via.placeholder.com/120'}
              alt={productId.name}
              className="cart-item-image"
            />
            <div className="cart-item-details">
              <h2>{productId.name}</h2>
              <p className="cart-item-description">
                {productId.description || ''}
              </p>
              <p>
                Price: <strong>${productId.price.toFixed(2)}</strong>
              </p>
              <p>
                Quantity:
                <button
                  disabled={updatingItemId === productId._id}
                  onClick={() => handleQuantityChange(productId._id, quantity - 1)}
                  className="qty-btn"
                >−</button>
                <span className="qty-value">{quantity}</span>
                <button
                  disabled={updatingItemId === productId._id}
                  onClick={() => handleQuantityChange(productId._id, quantity + 1)}
                  className="qty-btn"
                >+</button>
              </p>
              <p>
                Subtotal: <strong>${(productId.price * quantity).toFixed(2)}</strong>
              </p>
            </div>
            <button
              className="cart-remove-btn"
              onClick={() => handleRemove(productId._id)}
              disabled={removingItemId === productId._id}
            >
              {removingItemId === productId._id ? 'Removing...' : 'Remove'}
            </button>
          </div>
        ))}
      </div>

      <h2 className="cart-total">Total: ${totalPrice.toFixed(2)}</h2>

      {/* ✅ Checkout Button */}
      <button className="checkout-btn" onClick={handleCheckout}>
        Proceed to Checkout
      </button>
    </div>
  );
};

export default Cart;
