import React, { useEffect, useState } from 'react';
import '../../styles/MessageList.css';

const MessageList = () => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchMessages() {
      try {
        const res = await fetch('http://localhost:5000/api/message');
        if (!res.ok) throw new Error('Failed to fetch messages');
        const data = await res.json();
        setMessages(data.response || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchMessages();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this message?')) return;

    try {
      const res = await fetch(`http://localhost:5000/api/messages/${id}`, {
        method: 'DELETE'
      });
      if (!res.ok) throw new Error('Failed to delete');
      setMessages(prev => prev.filter(msg => msg._id !== id));
    } catch (err) {
      alert(err.message);
    }
  };

  if (loading) return <p className="loading">Loading messages...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="message-list-container">
      <h2>Customer Messages</h2>
      {messages.length === 0 ? (
        <p className="no-messages">No messages available.</p>
      ) : (
        <ul className="message-list">
          {messages.map(({ _id, name, email, message, date }) => (
            <li key={_id} className="message-card">
              <p><strong>Name:</strong> {name}</p>
              <p><strong>Email:</strong> {email}</p>
              <p><strong>Message:</strong> {message}</p>
              <p><strong>Date:</strong> {new Date(date).toLocaleString()}</p>
              <button onClick={() => handleDelete(_id)} className="delete-button">Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MessageList;
