import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/ProfilePage.css';

const ProfilePage = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isEditing, setIsEditing] = useState({ name: false, phone: false });
  const [editedUser, setEditedUser] = useState({ name: '', phone: '' });

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          setLoading(false);
          return;
        }

        const res = await axios.get('http://localhost:5000/api/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUser(res.data.user);
        setEditedUser({
          name: res.data.user.name,
          phone: res.data.user.phone,
        });
      } catch (error) {
        console.error('Error fetching profile:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleEditClick = (field) => {
    setIsEditing((prev) => ({ ...prev, [field]: true }));
  };

  const handleCancelClick = (field) => {
    setIsEditing((prev) => ({ ...prev, [field]: false }));
    // Reset the edited value to current user value to discard changes
    setEditedUser((prev) => ({
      ...prev,
      [field]: user[field],
    }));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedUser((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.put('http://localhost:5000/api/update-profile', editedUser, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setUser(res.data.updatedUser);
      setIsEditing({ name: false, phone: false });
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Failed to update profile. Please try again.');
    }
  };

  if (loading) {
    return (
      <div className="profile-container">
        <p>Loading profile...</p>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="profile-container">
        <h2>You are not logged in.</h2>
        <a href="/login">Go to Login</a>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <h2>My Profile</h2>
      <div className="profile-card">
        {/* Name */}
        <div className="profile-field">
          <strong>Name:</strong>
          {isEditing.name ? (
            <>
              <input
                type="text"
                name="name"
                value={editedUser.name}
                onChange={handleInputChange}
              />
              <button className="cancel-button" onClick={() => handleCancelClick('name')}>
                Cancel
              </button>
            </>
          ) : (
            <>
              <span>{user.name}</span>
              <button onClick={() => handleEditClick('name')}>✏️</button>
            </>
          )}
        </div>

        {/* Phone */}
        <div className="profile-field">
          <strong>Phone:</strong>
          {isEditing.phone ? (
            <>
              <input
                type="text"
                name="phone"
                value={editedUser.phone}
                onChange={handleInputChange}
              />
              <button className="cancel-button" onClick={() => handleCancelClick('phone')}>
                Cancel
              </button>
            </>
          ) : (
            <>
              <span>{user.phone}</span>
              <button onClick={() => handleEditClick('phone')}>✏️</button>
            </>
          )}
        </div>

        {/* Email (not editable) */}
        <div className="profile-field">
          <strong>Email:</strong> <span>{user.email}</span>
        </div>

        {/* Save button shows only if any field is editing */}
        {(isEditing.name || isEditing.phone) && (
          <button className="save-button" onClick={handleSave}>
            Save Changes
          </button>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
