import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/Profile.css';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [editedProfile, setEditedProfile] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem('jwtToken');
        const response = await axios.get('http://localhost:5000/api/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        setProfile(response.data.response);
        setEditedProfile(response.data.response); // for form values
      } catch (err) {
        console.error('Profile fetch error:', err);
        setError('Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setEditedProfile({ ...editedProfile, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      const token = localStorage.getItem('jwtToken');
      const response = await axios.put(
        `http://localhost:5000/api/profile/${profile._id}`,
        editedProfile,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setProfile(response.data.response);
      setEditMode(false);
    } catch (err) {
      console.error('Profile update error:', err);
      alert('Failed to update profile.');
    }
  };

  if (loading) return <p>Loading profile...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="profile-container">
      <h2>Your Profile</h2>

      {editMode ? (
        <>
          <p><strong>Name:</strong> <input name="name" value={editedProfile.name} onChange={handleChange} /></p>
          <p><strong>Phone:</strong> <input name="phone" value={editedProfile.phone} onChange={handleChange} /></p>
          <p><strong>Email:</strong> <input name="email" value={editedProfile.email} onChange={handleChange} /></p>
          <button onClick={handleSave}>Save</button>
          <button onClick={() => setEditMode(false)}>Cancel</button>
        </>
      ) : (
        <>
          <p><strong>Name:</strong> {profile.name}</p>
          <p><strong>Phone:</strong> {profile.phone}</p>
          <p><strong>Email:</strong> {profile.email}</p>
          <button onClick={() => setEditMode(true)}>Edit Profile</button>
        </>
      )}
    </div>
  );
};

export default Profile;
