import React, { useState, useEffect } from 'react';
import '../styles/Carousel.css';

const images = [
  'https://images.unsplash.com/photo-1580828343064-fde4fc206bc6?q=80&w=1171&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', 
  'https://plus.unsplash.com/premium_photo-1682096559740-9a5e45ef54bd?q=80&w=1432&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D', 
  'https://img.freepik.com/free-vector/hand-drawn-fashion-collection-twitch-banner_23-2149985384.jpg?ga=GA1.1.1632847897.1747477350&semt=ais_hybrid&w=740',
  'https://www.freepik.com/free-vector/flat-design-minimal-boutique-twitch-banner-template_23404783.htm#fromView=search&page=1&position=7&uuid=3b0cdaaf-5c4c-45e5-a693-1ba1e15010b2&query=fashion+banner',
];

const Carousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(timer);
  }, []);

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  return (
    <div className="carousel">
      <button className="carousel-btn prev" onClick={prevSlide} aria-label="Previous Slide">&#10094;</button>
      <img src={images[currentIndex]} alt={`Slide ${currentIndex + 1}`} />
      <button className="carousel-btn next" onClick={nextSlide} aria-label="Next Slide">&#10095;</button>

      <div className="carousel-indicators">
        {images.map((_, idx) => (
          <span
            key={idx}
            className={idx === currentIndex ? 'active' : ''}
            onClick={() => setCurrentIndex(idx)}
            aria-label={`Go to slide ${idx + 1}`}
            role="button"
            tabIndex={0}
            onKeyDown={(e) => {
              if (e.key === 'Enter' || e.key === ' ') setCurrentIndex(idx);
            }}
          />
        ))}
      </div>
    </div>
  );
};

export default Carousel;
