import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import '../styles/UpdateProduct.css';

const UpdateProduct = () => {
  const navigate = useNavigate();
  const { id } = useParams(); // used only for form view
  const location = useLocation();

  const [products, setProducts] = useState([]);
  const [editProduct, setEditProduct] = useState(null);
  const [formValues, setFormValues] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    imageUrl: '',
    stock: ''
  });

  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  // === Load all products if not editing ===
  useEffect(() => {
    if (!id) {
      fetch('http://localhost:5000/api/products')
        .then((res) => res.json())
        .then((data) => {
          setProducts(data.response || []);
          setLoading(false);
        })
        .catch((err) => {
          console.error(err);
          setError('Failed to load products');
          setLoading(false);
        });
    }
  }, [id]);

  // === If editing: load specific product ===
  useEffect(() => {
    if (id) {
      fetch(`http://localhost:5000/api/products/${id}`)
        .then((res) => {
          if (!res.ok) throw new Error('Product not found');
          return res.json();
        })
        .then((data) => {
          setFormValues(data.response || {});
          setEditProduct(data.response || {});
        })
        .catch((err) => {
          console.error(err);
          setError(err.message);
        })
        .finally(() => setLoading(false));
    }
  }, [id]);

  const handleEdit = (productId) => {
    navigate(`/admin/products/edit/${productId}`);
  };

  const handleChange = (e) => {
    setFormValues({ ...formValues, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await fetch(`http://localhost:5000/api/update-product/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formValues),
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.message || 'Update failed');
      }

      navigate('/admin/products'); 
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) return <p className="loading">Loading...</p>;
  if (error) return <p className="error">{error}</p>;

  // === View: Edit Form ===
  if (id && editProduct) {
    return (
      <div className="edit-product-container">
        <h2>Edit Product</h2>
        <form onSubmit={handleSubmit} className="edit-product-form">
          <input type="text" name="name" value={formValues.name} onChange={handleChange} placeholder="Product Name" required />
          <textarea name="description" value={formValues.description} onChange={handleChange} placeholder="Description" required />
          <input type="number" name="price" value={formValues.price} onChange={handleChange} placeholder="Price" required />
          <input type="text" name="category" value={formValues.category} onChange={handleChange} placeholder="Category" />
          <input type="text" name="imageUrl" value={formValues.imageUrl} onChange={handleChange} placeholder="Image URL" />
          <input type="number" name="stock" value={formValues.stock} onChange={handleChange} placeholder="Stock" required />
          {error && <p className="form-error">{error}</p>}
          <button type="submit" className="update-btn">Update Product</button>
        </form>
      </div>
    );
  }

  // === View: Product List ===
  return (
    <div className="all-products-container">
      <h2>All Products</h2>
      <div className="product-list">
        {products.map((product) => (
          <div className="product-card" key={product._id}>
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p><strong>Price:</strong> ${product.price}</p>
            <p><strong>Stock:</strong> {product.stock}</p>
            <p><strong>Category:</strong> {product.category}</p>
            <button onClick={() => handleEdit(product._id)}>Edit</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UpdateProduct;
