import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; 
import '../styles/UpdateOrder.css';

const UpdateOrder = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) throw new Error('Unauthorized: No token found');

      const res = await fetch('http://localhost:5000/api/orders', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.message || 'Failed to fetch orders');
      }

      const data = await res.json();
      setOrders(data.orders || []);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateClick = (orderId) => {
    navigate(`/admin/update-order-form/${orderId}`);
  };

  if (loading) return <p className="loading">Loading orders...</p>;
  if (error) return <p className="error-message">{error}</p>;

  return (
    <div className="update-order-container">
      <h2>All Orders</h2>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map(order => (
          <div key={order._id} className="order-card">
            <p><strong>Order ID:</strong> {order._id}</p>
            <p><strong>Payment:</strong> {order.paymentMethod || 'N/A'}</p>
            <p><strong>Status:</strong> {order.status}</p>
            <p><strong>Shipping:</strong> {order.shippingInfo?.name}, {order.shippingInfo?.address}</p>

            <ul>
              {order.items.map((item, idx) => (
                <li key={idx}>
                  {item.productId?.name || 'Product'} Ã— {item.quantity}
                </li>
              ))}
            </ul>

            <button onClick={() => handleUpdateClick(order._id)}>Update</button>
          </div>
        ))
      )}
    </div>
  );
};

export default UpdateOrder;
