import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/UpdateOrderForm.css';

const UpdateOrderForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [order, setOrder] = useState(null);
  const [status, setStatus] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await fetch(`http://localhost:5000/api/orders/${id}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        if (!res.ok) throw new Error('Failed to fetch order');
        const data = await res.json();
        setOrder(data.order);
        setStatus(data.order.status || '');
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchOrder();
  }, [id]);

  const handleStatusChange = (e) => {
    setStatus(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/update-order/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ status }),
      });
      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.message || 'Update failed');
      }
      alert('Order status updated successfully');
      navigate('/admin/update-order');
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) return <p>Loading order data...</p>;
  if (error) return <p style={{ color: 'red' }}>Error: {error}</p>;
  if (!order) return <p>No order found.</p>;

  return (
    <div className="update-order-form-container">
      <h2>Update Order Status</h2>
      <div className="order-details">
        <p><strong>Order ID:</strong> {order._id}</p>
        <p><strong>User:</strong> {order.user?.name || 'Guest'} ({order.user?.email || 'N/A'})</p>
        <p><strong>Payment Method:</strong> {order.paymentMethod}</p>
        <p><strong>Shipping Info:</strong> {order.shippingInfo?.name}, {order.shippingInfo?.address}</p>
        <p><strong>Items:</strong></p>
        <ul>
          {order.items.map((item, idx) => (
            <li key={idx}>
              {item.productId?.name || 'Product'} Ã— {item.quantity}
            </li>
          ))}
        </ul>
      </div>

      <form onSubmit={handleSubmit} className="update-order-form">
        <label htmlFor="status">Status:</label>
        <select id="status" value={status} onChange={handleStatusChange} required>
          <option value="">Select status</option>
          <option value="Pending">Pending</option>
          <option value="Processing">Processing</option>
          <option value="Shipped">Shipped</option>
          <option value="Delivered">Delivered</option>
          <option value="Cancelled">Cancelled</option>
        </select>

        <button type="submit">Update Status</button>
      </form>
    </div>
  );
};

export default UpdateOrderForm;
