import React, { useEffect, useState } from 'react';
import '../styles/AllMessages.css';

const AllMessages = () => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchMessages() {
      try {
        const res = await fetch('http://localhost:5000/api/message');
        if (!res.ok) throw new Error('Failed to fetch messages');
        const data = await res.json();
        setMessages(data.response || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchMessages();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this message?')) return;

    try {
      const res = await fetch(`http://localhost:5000/api/message/${id}`, {
        method: 'DELETE',
      });
      if (!res.ok) throw new Error('Failed to delete message');
      setMessages(messages.filter(msg => msg._id !== id));
    } catch (err) {
      alert(err.message);
    }
  };

  if (loading) return <p className="loading">Loading messages...</p>;
  if (error) return <p className="error-message">Error: {error}</p>;
  if (messages.length === 0) return <p className="no-message">No messages found.</p>;

  return (
    <div className="all-messages-container">
      <h2>All Messages</h2>
      <ul className="message-list">
        {messages.map(msg => (
          <li key={msg._id} className="message-item">
            <div className="message-content">
              <p><strong>From:</strong> {msg.senderName || 'Unknown'}</p>
              <p><strong>Email:</strong> {msg.email || 'N/A'}</p>
              <p><strong>Message:</strong> {msg.message || '-'}</p>
              <small>{new Date(msg.createdAt).toLocaleString()}</small>
            </div>
            <button className="delete-btn" onClick={() => handleDelete(msg._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AllMessages;
