import React, { useEffect, useState } from 'react';

const MyOrderPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        setLoading(true);
        setError('');
        const token = localStorage.getItem('token');

        const res = await fetch('http://localhost:5000/api/orders', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!res.ok) {
          throw new Error('Failed to fetch orders');
        }

        const data = await res.json();
        setOrders(data.orders || []);
      } catch (err) {
        setError(err.message || 'Something went wrong');
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) return <p>Loading your orders...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (orders.length === 0) return <p>You have no orders yet.</p>;

  return (
    <div style={{ maxWidth: 900, margin: '2rem auto', padding: '0 20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>My Orders</h1>
      {orders.map((order) => (
        <div
          key={order._id}
          style={{
            border: '1px solid #ddd',
            borderRadius: 10,
            padding: 20,
            marginBottom: 20,
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
          }}
        >
          <h2>Order ID: {order._id}</h2>
          <p>
            <strong>Order Date:</strong> {new Date(order.createdAt).toLocaleString()}
          </p>
          <p>
            <strong>Status:</strong> {order.status}
          </p>
          <p>
            <strong>Payment Method:</strong> {order.paymentMethod}
          </p>
          <h3>Shipping Info:</h3>
          <p>Name: {order.shippingInfo.name}</p>
          <p>Phone: {order.shippingInfo.phone}</p>
          <p>Address: {order.shippingInfo.address}</p>

          <h3>Items:</h3>
          <ul>
            {order.items.map(({ productId, quantity }) => (
              <li key={productId._id} style={{ marginBottom: 8 }}>
                <img
                  src={productId.imageUrl || 'https://via.placeholder.com/60'}
                  alt={productId.name}
                  style={{ width: 60, height: 60, objectFit: 'cover', borderRadius: 5, marginRight: 10 }}
                />
                <strong>{productId.name}</strong> — Quantity: {quantity} — Price: ${productId.price.toFixed(2)}
              </li>
            ))}
          </ul>

          <p style={{ fontWeight: 'bold', fontSize: '1.2rem', marginTop: 10 }}>
            Total Price: ${order.totalPrice.toFixed(2)}
          </p>
        </div>
      ))}
    </div>
  );
};

export default MyOrderPage;
