import React, { useState, useEffect } from 'react';
import { Routes, Route, useLocation } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import './styles/App.css';

import Navbar from './components/Navbar';
import Footer from './components/Footer';

import LoginPage from './pages/LoginPage';
import SignupPage from './pages/SignupPage';
import ProfilePage from './pages/ProfilePage';
import HomePage from './pages/HomePage';
import AllProduct from './pages/AllProduct';

// Admin imports updated according to folder structure
import AdminLayout from './admin/AdminLayout';
import Dashboard from './admin/pages/Dashboard';

// Products admin pages
import ProductList from './admin/pages/products/ProductList';
import AddProduct from './admin/pages/products/AddProduct';
import EditProduct from './admin/pages/products/EditProduct';

// Orders admin pages
import OrderList from './admin/pages/orders/OrderList';
import EditOrder from './admin/pages/orders/EditOrder';

// Messages admin page
import MessageList from './admin/pages/messages/MessageList';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
      return;
    }
    try {
      const decoded = jwtDecode(token);
      if (decoded.exp * 1000 < Date.now()) {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
      } else {
        setIsLoggedIn(true);
      }
    } catch (error) {
      localStorage.removeItem('token');
      setIsLoggedIn(false);
    }
  }, [location]);

  const isAdminRoute = location.pathname.startsWith('/admin');

  return (
    <div className="App">
      {!isAdminRoute && <Navbar isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />}

      <Routes>
        {/* Public routes */}
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginPage setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="/signup" element={<SignupPage />} />
        <Route path="/profile" element={<ProfilePage />} />
        <Route path="/products" element={<AllProduct />} />

        {/* Admin routes */}
        <Route path="/admin" element={<AdminLayout />}>
          <Route index element={<Dashboard />} />

          <Route path="products">
            <Route index element={<ProductList />} />
            <Route path="add" element={<AddProduct />} />
            <Route path="edit/:id" element={<EditProduct />} />
          </Route>

          <Route path="orders">
            <Route index element={<OrderList />} />
            <Route path="edit/:id" element={<EditOrder />} />
          </Route>

          <Route path="messages" element={<MessageList />} />
        </Route>
      </Routes>

      {!isAdminRoute && <Footer />}
    </div>
  );
}

export default App;
