import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/AllProduct.css';

function AllProduct() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const location = useLocation(); 

  useEffect(() => {
    async function fetchProducts() {
      try {
        const params = new URLSearchParams(location.search);
        const search = params.get('search');

        let url = 'http://localhost:5000/api/products';
        if (search) {
          url += `?search=${encodeURIComponent(search)}`;
        }

        const response = await fetch(url);
        if (!response.ok) throw new Error('Failed to fetch products');
        const data = await response.json();
        setProducts(data); 
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchProducts();
  }, [location.search]);

  if (loading) return <p className="loading">Loading products...</p>;
  if (error) return <p className="error">{error}</p>;
  if (products.length === 0) return <p className="no-products">No products found.</p>;

  return (
    <div className="all-product-container">
      <h1>All Products</h1>
      <div className="product-grid">
        {products.map(({ _id, name, description, price, category, imageUrl, stock }) => (
          <div key={_id} className="product-card">
            {imageUrl ? (
              <img src={imageUrl} alt={name} className="product-image" />
            ) : (
              <div className="placeholder-image">No Image</div>
            )}
            <div className="product-info">
              <h2 className="product-name">{name}</h2>
              <p className="product-category">{category || 'Uncategorized'}</p>
              <p className="product-description">{description || 'No description provided.'}</p>
              <p className="product-price">${price.toFixed(2)}</p>
              <p className={`product-stock ${stock > 0 ? 'in-stock' : 'out-stock'}`}>
                {stock > 0 ? `In Stock: ${stock}` : 'Out of Stock'}
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AllProduct;
