import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/UpdateOrder.css';

const UpdateOrder = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);
  const [status, setStatus] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchOrder() {
      try {
        const res = await fetch(`http://localhost:5000/api/orders/${id}`);
        if (!res.ok) throw new Error('Order not found');
        const data = await res.json();
        setOrder(data.response);
        setStatus(data.response.status);
      } catch (err) {
        setError(err.message);
      }
    }

    fetchOrder();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch(`http://localhost:5000/api/orders/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status }),
      });

      if (!res.ok) throw new Error('Failed to update order');
      alert('Order updated successfully');
      navigate('/admin/orders');
    } catch (err) {
      alert(err.message);
    }
  };

  if (error) return <p className="error">{error}</p>;
  if (!order) return <p className="loading">Loading order details...</p>;

  return (
    <div className="edit-order-container">
      <h2>Edit Order</h2>
      <div className="order-details">
        <p><strong>Order ID:</strong> {order._id}</p>
        <p><strong>User:</strong> {order.user?.name || 'Anonymous'}</p>
        <p><strong>Total:</strong> ${order.totalAmount.toFixed(2)}</p>
        <p><strong>Current Status:</strong> {order.status}</p>
      </div>

      <form onSubmit={handleSubmit} className="edit-order-form">
        <label htmlFor="status">Update Status:</label>
        <select
          id="status"
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        >
          <option value="pending">Pending</option>
          <option value="processing">Processing</option>
          <option value="shipped">Shipped</option>
          <option value="delivered">Delivered</option>
        </select>

        <button type="submit" className="update-button">Update Order</button>
      </form>
    </div>
  );
};

export default UpdateOrder;
