// admin/pages/UpdateOrder.jsx
import React, { useEffect, useState } from 'react';
import '../styles/UpdateOrder.css';

const UpdateOrder = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const res = await fetch('http://localhost:5000/api/orders');
      if (!res.ok) throw new Error('Failed to fetch orders');
      const data = await res.json();
      setOrders(data.orders || []);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleStatusUpdate = async (orderId, newStatus) => {
    try {
      const res = await fetch(`http://localhost:5000/api/orders/${orderId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: newStatus })
      });

      if (!res.ok) throw new Error('Failed to update status');
      fetchOrders(); 
    } catch (err) {
      alert(err.message);
    }
  };

  if (loading) return <p className="loading">Loading orders...</p>;
  if (error) return <p className="error-message">{error}</p>;

  return (
    <div className="update-order-container">
      <h2>All Orders</h2>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map(order => (
          <div key={order._id} className="order-card">
            <p><strong>Order ID:</strong> {order._id}</p>
            <p><strong>User:</strong> {order.user?.name || 'Guest'} ({order.user?.email || 'N/A'})</p>
            <p><strong>Items:</strong></p>
            <ul>
              {order.items.map((item, idx) => (
                <li key={idx}>{item.name} × {item.quantity} — ${item.price}</li>
              ))}
            </ul>
            <p><strong>Total:</strong> ${order.total}</p>
            <p><strong>Payment:</strong> {order.paymentMethod || 'N/A'}</p>
            <p><strong>Address:</strong> {order.shippingAddress || 'N/A'}</p>
            <p><strong>Status:</strong> {order.status}</p>

            <label htmlFor={`status-${order._id}`}>Update Status:</label>
            <select
              id={`status-${order._id}`}
              value={order.status}
              onChange={(e) => handleStatusUpdate(order._id, e.target.value)}
            >
              <option value="Pending">Pending</option>
              <option value="Processing">Processing</option>
              <option value="Shipped">Shipped</option>
              <option value="Delivered">Delivered</option>
              <option value="Cancelled">Cancelled</option>
            </select>
          </div>
        ))
      )}
    </div>
  );
};

export default UpdateOrder;
