import React, { useState } from 'react';
import axios from 'axios';

function AdminForm() {
  const [product, setProduct] = useState({
    name: '', description: '', price: '', category: '', imageUrl: '', stock: ''
  });

  const handleChange = e => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/admin/add-product', product);
      alert('Product added successfully!');
      setProduct({ name: '', description: '', price: '', category: '', imageUrl: '', stock: '' });
    } catch (err) {
      console.error(err);
      alert('Error adding product');
    }
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h2 className="text-xl font-bold mb-4">Add Product</h2>
      <form onSubmit={handleSubmit} className="space-y-3">
        {['name', 'description', 'price', 'category', 'imageUrl', 'stock'].map(field => (
          <input key={field} type="text" name={field} value={product[field]} onChange={handleChange} placeholder={field} className="w-full p-2 border rounded" required />
        ))}
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Add Product</button>
      </form>
    </div>
  );
}

export default AdminForm;
